import {Component, OnInit, ViewChild} from '@angular/core';
import {CountryListServiceService} from "./country-list-service.service";
import {CountryListModel} from "./country-list-model.model";
import {FormsModule, FormControl, NgForm} from "@angular/forms";
import {ApiRespons} from "../../../model/employees/api-respons";


@Component({
  selector: 'app-country-list',
  templateUrl: './country-list.component.html',
  styleUrls: ['./country-list.component.css']
})

export class CountryListComponent implements OnInit {

@ViewChild("clForm")clForm: NgForm;
  constructor(
    private service: CountryListServiceService,

  ) { }

  apir: ApiRespons;
  country: CountryListModel[] = [];
  coun: CountryListModel = new CountryListModel();
  countrySave = false;

  ngOnInit(): void {
    this.countryList();
  }

  countryList(){
    this.service.getAllCountry().subscribe(
      country => {
        this.country = country
        console.log(this.country)
      }
    )
  }

  addNewCountry(clForm: NgForm){

    this.coun = clForm.value;
    this.service.createCountry(this.coun).subscribe(
      data => {
        clForm.reset();
        this.countrySave = true;
        this.countryList();
      }

    )
    this.ngOnInit();
  }

}


<main id="main" class="main">

    <div class="pagetitle">
        <h1 class="text-center">All Country</h1>

    </div><!-- End Page Title -->

    <section class="section">
        <div class="row">
            <div class="col-md-10 offset-1">
                <div class="card">

                    <div class="card-body mt-3">
                      <div style="background-color: rgba(0,255,0,0.4)" *ngIf="countrySave">
                        <p>Country Add succsfully</p>
                      </div>
                        <form  (ngSubmit)="addNewCountry(clForm)" #clForm="ngForm">
                            <input type="hidden" >
                            <div class="row mb-3">
                                <label class="col-sm-2 col-form-label">Country Name</label>
                                <div class="col-sm-10">
                                    <input type="text" class="form-control" name="cntName" ngModel/>
                                </div>
                            </div>
                            <div class="row mb-3">
                                <label class="col-sm-2 col-form-label">Country Short Name</label>
                                <div class="col-sm-10">
                                    <input type="text" class="form-control" name="cntShortCode" ngModel/>
                                </div>
                            </div>
                            <div class="row mb-3">
                                <div class="col-md-12 text-center">
<!--                                    <button  type="submit" class="btn btn-primary">Update</button>-->
                                    <button  type="submit" class="btn btn-primary">Add Country</button>

                                </div>
                            </div>
                        </form>
                    </div>


                    <div class="card-body">

                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">ID</th>
                                    <th scope="col">Country Name</th>
                                    <th scope="col">Country Short Name</th>
                                    <th scope="col">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr *ngFor="let cl of country; index as i;">
                                    <td>{{i+1}}</td>
                                    <td>{{cl.cntName}}</td>
                                    <td>{{cl.cntShortCode}}</td>

                                    <td>
                                        <a  class="btn btn-danger">Delete</a>
                                        <a  class="btn btn-warning">Edit</a>
                                    </td>
                                </tr>

                            </tbody>
                        </table>
                        <!-- End Default Table Example -->
                    </div>
                </div>

            </div>

        </div>
    </section>

</main><!-- End #main -->
